// loader.js
import gsap from 'gsap';

const tl = gsap.timeline();
tl.to(".animation",{
    fontSize: "30px",
    position: "absolute",
    height: "100px",
    width: "150px",
    duration: 3,
    delay: 2
});
tl.to(".txt",{
    opacity: 1,
    duration: 1,
    stagger: .25
})
tl.to(".ul",{
    opacity: 1,
    duration: 1,
    stagger: .25
});
