import React from "react";
import transition from "../transition";
import Navbar from "./Navbar.jsx";
import GamingCard from "../assets/GamingCard.png"
import { useEffect, useRef, useState } from "react";

const Eventpage = () => {
  const [showNavbar, setShowNavbar] = useState(false);
  const mainRef = useRef(null);

  useEffect(() => {
    const createDiv = () => {
      const scene = document.querySelector(".scene");
      for (let i = 0; i < 210; i++) {
        const div = document.createElement("div");
        scene.appendChild(div);
      }
    };

    createDiv();

    const setStarsProperties = () => {
      const stars = document.querySelectorAll(".scene div");
      stars.forEach((star) => {
        let x = `${Math.random() * 200}vmax`;
        let y = `${Math.random() * 100}vh`;
        let z = `${Math.random() * 200 - 100}vmin`;
        let rx = `${Math.random() * 360}deg`;
        star.style.setProperty("--x", x);
        star.style.setProperty("--y", y);
        star.style.setProperty("--z", z);
        star.style.setProperty("--rx", rx);
        let delay = `${Math.random() * 1.5}s`;
        star.style.animationDelay = delay;
      });
    };

    setStarsProperties();
    setTimeout(() => {
      setShowNavbar(true);
      const mainElement = mainRef.current;
      mainElement.classList.add("blurred");
    }, 3000);
  }, []);

  return (
    <div>
      {showNavbar && <Navbar />}

      <div className="flex flex-col mx-auto absolute z-10 ml-[2rem]">
        <div className="flex flex-row mx-auto mt-[5rem] justify-between items-center ">
          <div className="flex justify-center items-center align-middle pr-2">
            <img src={GamingCard} className="w-[40rem] h-[20rem]"></img>
          </div>
          <div className="flex justify-center items-center align-middle">
            <img src={GamingCard} className="w-[40rem] h-[20rem] "></img>
          </div>
        </div>
        <div className="flex flex-row mx-auto pt-2 justify-between items-center">
          <div className="flex justify-center items-center align-middle pr-2">
            <img src={GamingCard} className="w-[40rem] h-[18rem] "></img>
          </div>
          <div className="flex justify-center items-center align-middle">
            <img src={GamingCard} className="w-[40rem] h-[20rem] "></img>
          </div>
        </div>
       
      </div>

      <main
        ref={mainRef}
        className="scene main-container"
        style={{ position: "relative", zIndex: 0 }}
      ></main>
    </div>
  );
};

export default transition(Eventpage);
